// CheckoutPage.js
import React, { useState, useEffect } from 'react';
import { getCartItems, processPayment } from '../services/paymentService'; // Adjust the path as needed
import { applyCoupons } from '../services/couponService'; // Adjust the path as needed

const CheckoutPage = () => {
    const [cart, setCart] = useState([]);
    const [total, setTotal] = useState(0);
    const [loading, setLoading] = useState(false);
    const [paymentSuccess, setPaymentSuccess] = useState(false);

    useEffect(() => {
        const cartItems = getCartItems();
        setCart(cartItems);
        const totalAmount = cartItems.reduce((sum, item) => sum + item.price, 0);
        setTotal(totalAmount);
    }, []);

    const handleApplyCoupons = async () => {
        try {
            setLoading(true);
            const updatedCart = await applyCoupons(cart);
            setCart(updatedCart);
            setTotal(updatedCart.total);
        } catch (error) {
            alert('Failed to apply coupons. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    const handleCheckout = async () => {
        try {
            setLoading(true);
            const paymentResult = await processPayment(cart);
            if (paymentResult.status === 'succeeded') {
                setPaymentSuccess(true);
                alert('Payment successful!');
            }
        } catch (error) {
            alert('Payment failed. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="checkout-container">
            <h1>Your Cart</h1>
            <ul>
                {cart.map((item, index) => (
                    <li key={index}>{item.name} - ${item.price}</li>
                ))}
            </ul>
            <h2>Total: ${total}</h2>
            <button onClick={handleApplyCoupons} disabled={loading}>
                {loading ? 'Applying Coupons...' : 'Apply Coupons'}
            </button>
            <button onClick={handleCheckout} disabled={loading}>
                {loading ? 'Processing Payment...' : 'Checkout'}
            </button>
            {paymentSuccess && <p>Payment Completed Successfully!</p>}
        </div>
    );
};

export default CheckoutPage;
